import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Sidebar from '../components/dashboardStudent/Sidebar';
import '../pagesCSS/Studentdashboard.css';

function StudentDashboard() {
  const navigate = useNavigate();

  // États
  const [user, setUser] = useState(null);
  const [cvHistory, setCvHistory] = useState([
    { id: 1, name: 'CV_January_2025.pdf', score: 78, date: '2025-01-15' },
    { id: 2, name: 'CV_Updated.pdf', score: 65, date: '2025-01-10' }
  ]);
  const [chatHistory] = useState([
    { id: 1, title: 'CV Improvement Tips', date: '2025-01-14' },
    { id: 2, title: 'Interview Preparation', date: '2025-01-12' }
  ]);

  // Charger le user
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      navigate('/student/login');
    }
  }, [navigate]);

  // Fonction logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="student-dashboard">
      <Header />

      <div className="dashboard-layout">
        <Sidebar 
          user={user}
          cvHistory={cvHistory}
          chatHistory={chatHistory}
          onLogout={handleLogout}
        />

        <main className="dashboard-main">
          <h1>Main Content</h1>
        </main>
      </div>
    </div>
  );
}

export default StudentDashboard;
