function Sidebar({user,cvHistory,chatHistory}) {
    return (
        <aside className="dashboardStudent-sidebar">
            <button className="sidebar-toggle">‚Üê</button>
            <div className="sidebar-user">
        <div className="user-avatar">
          {user?.fullName?.[0]?.toUpperCase()}
        </div>
        <div className="user-info">
          <h3>{user?.fullName}</h3>
          <p>{user?.email}</p>
        </div>
      </div>

      {/* CV History - re√ßu en props */}
      <div className="sidebar-section">
        <h4>üìÑ CV History</h4>
        <div className="history-list">
          {cvHistory?.map(cv => (
            <div key={cv.id} className="history-item">
              <p>{cv.name}</p>
              <p>Score: {cv.score}%</p>
            </div>
          ))}
        </div>
      </div>

      {/* Chat History - re√ßu en props */}
      <div className="sidebar-section">
        <h4>üí¨ Chat History</h4>
        <div className="history-list">
          {chatHistory?.map(chat => (
            <div key={chat.id} className="history-item">
              <p>{chat.title}</p>
            </div>
          ))}
        </div>
      </div>

      <button className="logout-btn">üö™ Logout</button>
        </aside>
    );
}

export default Sidebar;
