import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import '../pagesCSS/Studentdashboard.css';

function StudentDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeStep, setActiveStep] = useState(null); // 'scoring', 'advice', 'matching'
  const [cvUploaded, setCvUploaded] = useState(false);
  const [cvScore, setCvScore] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    { role: 'assistant', content: 'Hi! I\'m your AI Career Coach. How can I help you today?' }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [chatLoading, setChatLoading] = useState(false);

  // History states
  const [cvHistory, setCvHistory] = useState([
    { id: 1, name: 'CV_January_2025.pdf', score: 78, date: '2025-01-15', time: '14:30' },
    { id: 2, name: 'CV_Updated.pdf', score: 65, date: '2025-01-10', time: '09:15' }
  ]);
  const [chatHistory] = useState([
    { id: 1, title: 'CV Improvement Tips', date: '2025-01-14', preview: 'How can I improve my CV for tech...' },
    { id: 2, title: 'Interview Preparation', date: '2025-01-12', preview: 'What should I prepare for...' }
  ]);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      navigate('/student/login');
    }
  }, [navigate]);

  // Handle file upload
  const handleFileUpload = (file) => {
    if (file && (file.type === 'application/pdf' || file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')) {
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }
      setUploadedFile(file);
      setCvUploaded(true);

      // Simulate CV analysis (later will be replaced with backend call)
      setTimeout(() => {
        const randomScore = Math.floor(Math.random() * 40) + 55; // Score between 55-95
        setCvScore(randomScore);

        // Add to history
        const newCV = {
          id: cvHistory.length + 1,
          name: file.name,
          score: randomScore,
          date: new Date().toISOString().split('T')[0],
          time: new Date().toTimeString().split(' ')[0].slice(0, 5)
        };
        setCvHistory([newCV, ...cvHistory]);
      }, 2000);
    } else {
      alert('Please upload a PDF or DOCX file');
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileUpload(e.dataTransfer.files[0]);
    }
  };

  const handleFileInputChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFileUpload(e.target.files[0]);
    }
  };

  // Handle step clicks
  const handleStepClick = (step) => {
    if (!cvUploaded) {
      alert('⚠️ Please upload your CV first before accessing this feature');
      return;
    }

    if (step === 'matching' && cvScore < 60) {
      alert('⚠️ Please check AI Resume Advice and improve your CV before applying to jobs (Score must be ≥ 60)');
      return;
    }

    setActiveStep(step);
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  // Handle chat message send
  const handleSendMessage = async () => {
    if (!chatInput.trim()) return;

    const userMessage = { role: 'user', content: chatInput };
    setChatMessages([...chatMessages, userMessage]);
    setChatInput('');
    setChatLoading(true);

    try {
      // TODO: Replace with actual API call to backend
      // const response = await fetch('http://localhost:5000/api/chat', {
      //   method: 'POST',
      //   headers: {
      //     'Content-Type': 'application/json',
      //     'Authorization': `Bearer ${localStorage.getItem('token')}`
      //   },
      //   body: JSON.stringify({ message: chatInput })
      // });
      // const data = await response.json();

      // Simulate AI response
      setTimeout(() => {
        const aiResponse = {
          role: 'assistant',
          content: 'This is a simulated response. Backend integration coming soon!'
        };
        setChatMessages(prev => [...prev, aiResponse]);
        setChatLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Chat error:', error);
      setChatLoading(false);
    }
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="student-dashboard">
      <Header />

      <div className="dashboard-layout">
        {/* SIDEBAR */}
        <aside className={`dashboard-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
          <button className="sidebar-toggle" onClick={() => setSidebarOpen(!sidebarOpen)}>
            {sidebarOpen ? '←' : '→'}
          </button>

          {sidebarOpen && (
            <div className="sidebar-content">
              {/* User Info */}
              <div className="sidebar-user">
                <div className="user-avatar">{user.fullName?.[0]?.toUpperCase()}</div>
                <div className="user-info">
                  <h3>{user.fullName}</h3>
                  <p>{user.email}</p>
                </div>
              </div>

              {/* CV History */}
              <div className="sidebar-section">
                <h4>📄 CV History</h4>
                <div className="history-list">
                  {cvHistory.map(cv => (
                    <div key={cv.id} className="history-item">
                      <div className="history-icon">📄</div>
                      <div className="history-details">
                        <p className="history-name">{cv.name}</p>
                        <p className="history-meta">
                          Score: <span className={cv.score >= 60 ? 'score-good' : 'score-low'}>{cv.score}%</span>
                        </p>
                        <p className="history-date">{cv.date} • {cv.time}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Chat History */}
              <div className="sidebar-section">
                <h4>💬 Chat History</h4>
                <div className="history-list">
                  {chatHistory.map(chat => (
                    <div key={chat.id} className="history-item">
                      <div className="history-icon">💬</div>
                      <div className="history-details">
                        <p className="history-name">{chat.title}</p>
                        <p className="history-preview">{chat.preview}</p>
                        <p className="history-date">{chat.date}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Logout Button */}
              <button className="logout-btn" onClick={handleLogout}>
                🚪 Logout
              </button>
            </div>
          )}
        </aside>

        {/* MAIN CONTENT */}
        <main className="dashboard-main">
          {activeStep === null ? (
            <>
              {/* Welcome Section */}
              <div className="welcome-section">
                <h1>Welcome to STAGII! 👋</h1>
                <p>Let's start by uploading your CV to find the perfect internship</p>
              </div>

              {/* Upload CV Card */}
              <div className="upload-card">
                {!cvUploaded ? (
                  <div
                    className={`upload-area ${dragActive ? 'drag-active' : ''}`}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                  >
                    <div className="upload-icon">☁️</div>
                    <h2>Upload Your CV</h2>
                    <p>Drag and drop or click to browse</p>
                    <p className="upload-hint">PDF, DOCX - Max 5MB</p>
                    <input
                      type="file"
                      id="cv-upload"
                      accept=".pdf,.docx"
                      onChange={handleFileInputChange}
                      style={{ display: 'none' }}
                    />
                    <label htmlFor="cv-upload" className="upload-btn">
                      📤 Browse Files
                    </label>
                  </div>
                ) : (
                  <div className="upload-success">
                    <div className="success-icon">✅</div>
                    <h3>CV Uploaded Successfully!</h3>
                    <p className="uploaded-file-name">📄 {uploadedFile?.name}</p>
                    {cvScore !== null && (
                      <div className="quick-score">
                        <span>Your CV Score: </span>
                        <span className={`score-badge ${cvScore >= 60 ? 'score-good' : 'score-low'}`}>
                          {cvScore}%
                        </span>
                      </div>
                    )}
                    <button
                      className="upload-another-btn"
                      onClick={() => {
                        setCvUploaded(false);
                        setUploadedFile(null);
                        setCvScore(null);
                        setActiveStep(null);
                      }}
                    >
                      Upload Another CV
                    </button>
                  </div>
                )}
              </div>

              {/* Steps Section - Connected Circles */}
              <div className="steps-section">
                <h2 className="steps-title">Your Journey to Success</h2>
                <p className="steps-subtitle">
                  Choose a sample resume below and click "CV Scoring (ATS)" to see our AI-powered analysis
                </p>

                <div className="steps-container">
                  {/* Step 1: CV Scoring */}
                  <div
                    className={`step-circle ${!cvUploaded ? 'disabled' : ''} ${activeStep === 'scoring' ? 'active' : ''}`}
                    onClick={() => handleStepClick('scoring')}
                  >
                    <div className="step-circle-inner">
                      <div className="step-icon">📊</div>
                      <h3>CV Scoring (ATS)</h3>
                      <p>Analyze your resume with AI-powered insights</p>
                    </div>
                  </div>

                  {/* Connector Line */}
                  <div className="step-connector"></div>

                  {/* Step 2: AI Resume Advice */}
                  <div
                    className={`step-circle ${!cvUploaded ? 'disabled' : ''} ${activeStep === 'advice' ? 'active' : ''}`}
                    onClick={() => handleStepClick('advice')}
                  >
                    <div className="step-circle-inner">
                      <div className="step-icon">💡</div>
                      <h3>AI Resume Advice</h3>
                      <p>Get professional advice to improve your CV</p>
                    </div>
                  </div>

                  {/* Connector Line */}
                  <div className="step-connector"></div>

                  {/* Step 3: Job Matching */}
                  <div
                    className={`step-circle ${!cvUploaded ? 'disabled' : ''} ${activeStep === 'matching' ? 'active' : ''}`}
                    onClick={() => handleStepClick('matching')}
                  >
                    <div className="step-circle-inner">
                      <div className="step-icon">🎯</div>
                      <h3>Job Matching Score</h3>
                      <p>Find your perfect job match with smart algorithms</p>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            <div className="step-detail-view">
              <button className="back-btn" onClick={() => setActiveStep(null)}>
                ← Back to Dashboard
              </button>

              {activeStep === 'scoring' && (
                <div className="scoring-view">
                  <h2>📊 CV Scoring (ATS)</h2>
                  <div className="score-circle-large">
                    <svg width="200" height="200">
                      <circle
                        cx="100"
                        cy="100"
                        r="80"
                        stroke="#e5e7eb"
                        strokeWidth="15"
                        fill="none"
                      />
                      <circle
                        cx="100"
                        cy="100"
                        r="80"
                        stroke={cvScore >= 60 ? '#10b981' : '#ef4444'}
                        strokeWidth="15"
                        fill="none"
                        strokeDasharray={`${(cvScore / 100) * 502.4} 502.4`}
                        strokeLinecap="round"
                        transform="rotate(-90 100 100)"
                      />
                    </svg>
                    <div className="score-text">
                      <span className="score-number">{cvScore}</span>
                      <span className="score-percent">%</span>
                    </div>
                  </div>
                  <div className="score-message">
                    {cvScore >= 60 ? (
                      <p>✅ Good job! You can check <strong>AI Resume Advice</strong> to further improve your CV</p>
                    ) : (
                      <p>⚠️ You should check <strong>AI Resume Advice</strong> to improve your CV before applying</p>
                    )}
                  </div>
                </div>
              )}

              {activeStep === 'advice' && (
                <div className="advice-view">
                  <h2>💡 AI Resume Advice</h2>
                  <p className="advice-intro">Based on your CV analysis, here are personalized recommendations:</p>
                  <div className="advice-cards">
                    <div className="advice-card">
                      <span className="advice-icon">📝</span>
                      <h3>Add Quantifiable Achievements</h3>
                      <p>Include metrics like "Improved app performance by 40%" to showcase impact</p>
                    </div>
                    <div className="advice-card">
                      <span className="advice-icon">🎯</span>
                      <h3>Highlight Technical Skills</h3>
                      <p>List specific technologies and tools you've worked with (e.g., React, Python, AWS)</p>
                    </div>
                    <div className="advice-card">
                      <span className="advice-icon">✨</span>
                      <h3>Optimize Keywords</h3>
                      <p>Include industry-specific keywords to pass ATS (Applicant Tracking Systems)</p>
                    </div>
                    <div className="advice-card">
                      <span className="advice-icon">📚</span>
                      <h3>Add Relevant Coursework</h3>
                      <p>Include certifications and relevant courses to strengthen your profile</p>
                    </div>
                  </div>
                </div>
              )}

              {activeStep === 'matching' && cvScore >= 60 && (
                <div className="matching-view">
                  <h2>🎯 Job Matching Results</h2>
                  <p className="matching-intro">Based on your CV, here are the best internship matches for you:</p>

                  <div className="jobs-grid">
                    <div className="job-card">
                      <div className="job-header">
                        <div className="company-logo">TC</div>
                        <div className="job-info">
                          <h3>Frontend Developer Intern</h3>
                          <p className="company-name">TechCorp Tunisia</p>
                        </div>
                        <div className="match-badge high">85% Match</div>
                      </div>
                      <div className="job-details">
                        <p>📍 Tunis, Tunisia • ⏱️ 3 months</p>
                        <div className="skills-match">
                          <strong>Matching Skills:</strong>
                          <div className="skills-tags">
                            <span>React</span>
                            <span>JavaScript</span>
                            <span>HTML/CSS</span>
                            <span>Git</span>
                          </div>
                        </div>
                      </div>
                      <button className="apply-btn">Apply Now →</button>
                    </div>

                    <div className="job-card">
                      <div className="job-header">
                        <div className="company-logo">DS</div>
                        <div className="job-info">
                          <h3>UI/UX Design Intern</h3>
                          <p className="company-name">DesignStudio</p>
                        </div>
                        <div className="match-badge medium">78% Match</div>
                      </div>
                      <div className="job-details">
                        <p>📍 Sfax, Tunisia • ⏱️ 3 months</p>
                        <div className="skills-match">
                          <strong>Matching Skills:</strong>
                          <div className="skills-tags">
                            <span>Figma</span>
                            <span>Adobe XD</span>
                            <span>User Research</span>
                          </div>
                        </div>
                      </div>
                      <button className="apply-btn">Apply Now →</button>
                    </div>

                    <div className="job-card">
                      <div className="job-header">
                        <div className="company-logo">DT</div>
                        <div className="job-info">
                          <h3>Data Science Intern</h3>
                          <p className="company-name">DataTech</p>
                        </div>
                        <div className="match-badge medium">72% Match</div>
                      </div>
                      <div className="job-details">
                        <p>📍 Tunis, Tunisia • ⏱️ 4 months</p>
                        <div className="skills-match">
                          <strong>Matching Skills:</strong>
                          <div className="skills-tags">
                            <span>Python</span>
                            <span>Pandas</span>
                            <span>SQL</span>
                          </div>
                        </div>
                      </div>
                      <button className="apply-btn">Apply Now →</button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </main>
      </div>

      {/* FLOATING CHAT BUTTON */}
      <button className="chat-float-btn" onClick={() => setShowChat(!showChat)}>
        💬
      </button>

      {/* CHAT MODAL */}
      {showChat && (
        <div className="chat-modal">
          <div className="chat-header">
            <h3>🤖 Chat with AI Coach</h3>
            <button className="chat-close" onClick={() => setShowChat(false)}>×</button>
          </div>
          <div className="chat-messages">
            {chatMessages.map((msg, idx) => (
              <div key={idx} className={`chat-message ${msg.role}`}>
                <div className="message-avatar">
                  {msg.role === 'user' ? '👤' : '🤖'}
                </div>
                <div className="message-content">{msg.content}</div>
              </div>
            ))}
            {chatLoading && (
              <div className="chat-message assistant">
                <div className="message-avatar">🤖</div>
                <div className="message-content typing">Typing...</div>
              </div>
            )}
          </div>
          <div className="chat-input-area">
            <input
              type="text"
              placeholder="Ask me anything about your CV or career..."
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default StudentDashboard;
